import { useState } from 'react';
import './App.css';
import Axios from 'axios';

function App() {

  const[name, setName] = useState('')
  const[age, setAge] = useState(0)
  const[pozition, setPozition] = useState('')
  const[country, setCountry] = useState('')
  const[wage, setWge] = useState(0)
  const[list, setList] = useState([])

  const addEmployee = () => {
    Axios.post('http://localhost:3001/create', {
      name: name,
      age: age,
      pozition: pozition,
      country: country,
      wage: wage
    }).then(() => {
      console.log("succes")
    })
  }

  const getList = () =>{
    Axios.get('http://localhost:3001/list', {

    }).then((response) => {
      setList(response.data)
    })
  }

  return (
    <div className="App">
      <div className='information'>
        <label>Name:</label>
        <input type="text" 
        onChange={(event) => {setName(event.target.value)}}></input>
        <label>Age:</label>
        <input type="number"
        onChange={(event) => {setAge(event.target.value)}}></input>
        <label>Pozition:</label>
        <input type="text"
        onChange={(event) => {setPozition(event.target.value)}}></input>
        <label>Country:</label>
        <input type="text"
        onChange={(event) => {setCountry(event.target.value)}}></input>
        <label>Wage (year)</label>
        <input type="number"
        onChange={(event) => {setWge(event.target.value)}}></input>
        <button onClick={addEmployee}>Send</button>
      </div>
      <div className='employees'>
        <button onClick={getList}>Show employees</button>
        {list.map((val, key) => {
          return <div className='list'> {val.name} </div>
        })}
      </div>
    </div>
  );
}

export default App;
